    /**
     * Returns results for interval future invoice
     *
     * @param string  $type
     * @param integer $companystryctyre
     * 
     * @return mixed[]
     */
    public function getInvoiceEmptyData ($type, $companystryctyre)
    {
        $res = $this->createQueryBuilder('i');
        /** select */
        $this->selectInvoicePeriod($res);
        /** join */
        $this->joinInvoicePeriod($res);
        if ($companystryctyre) {
            $res
                ->leftJoin('i.invoicecompanystructure', 'i_ics')
                ->andWhere(
                    '
                        i_ics.companystructureId = (:companystructureId) 
                        or
                        i_ics.companystructureId in 
                            (
                                SELECT
                                    cc.id
                                FROM
                                    ListsCompanystructureBundle:Companystructure cp
                                LEFT JOIN 
                                    ListsCompanystructureBundle:Companystructure cc 
                                WHERE
                                    cp.root = cc.root
                                AND
                                    cp.lft < cc.lft
                                AND 
                                    cp.rgt > cc.rgt
                                AND
                                    cp in (:companystructureId)
                            )
                    '
                )
                ->setParameter(':companystructureId', $companystryctyre);
        }
        /** where */
        switch ($type) {
            case 'delay':
                $res = $res->andWhere("i.delayDays is NULL or i.delayDays = 0")
                    ->andWhere("i.dateFact is NULL")
                    ->orderBy('performerName, i.id', 'DESC');
                break;
            case 'act':
                $res = $res
                ->andWhere(
                    "i.id in (
                        SELECT iao.id
                        FROM ITDoorsControllingBundle:Invoice iao
                        INNER JOIN ITDoorsControllingBundle:InvoiceAct ia
                        WHERE iao.id = ia.invoiceId
                        AND ia.original = false
                    )"
                )
                ->andWhere("i.dateFact is NULL")
                ->orderBy('performerName, i.id', 'DESC');
                break;
            case 'dogovor':
                $res = $res->andWhere("i.dogovorId is NULL")
                    ->andWhere("i.dateFact is NULL")
                    ->orderBy('performerName, i.id', 'DESC');
                break;
            case 'toFriday':
                $dateStart = date('Y-m-d');
                $dateStop = date('Y-m-d', mktime(0, 0, 0, date("m"), date('d') + 5 - date("w"), date('Y')));
                $res
                    ->andWhere("i.delayDate BETWEEN  :dateStart AND :dateStop")
                    ->setParameter(':dateStart', $dateStart)
                    ->setParameter(':dateStop', $dateStop)
                    ->orderBy('performerName', 'DESC');
                break;
            case 'nextWeek':
                $days = 0;
                if (date("w") == 0) {
                    $days = 1;
                } elseif (date("w") == 6) {
                    $days = 2;
                } elseif (date("w") == 5) {
                    $days = 3;
                }
                $dateStart = date('Y-m-d', mktime(0, 0, 0, date("m"), date('d') + $days, date('Y')));
                $dateStop = date('Y-m-d', mktime(0, 0, 0, date("m"), date('d') + 5 + $days, date('Y')));
                $res
                    ->andWhere("i.delayDate BETWEEN  :dateStart AND :dateStop")
                    ->setParameter(':dateStart', $dateStart)
                    ->setParameter(':dateStop', $dateStop)
                    ->orderBy('performerName', 'DESC');
                break;
            case 'monday':
                $days = 0;
                if (date("w") == 0) {
                    $days = 1;
                } elseif (date("w") == 6) {
                    $days = 2;
                } elseif (date("w") == 5) {
                    $days = 3;
                }
                $date = date('Y-m-d', mktime(0, 0, 0, date("m"), date('d') + $days, date('Y')));
                $res
                    ->andWhere("i.delayDate = :date")
                    ->setParameter(':date', $date)
                    ->orderBy('performerName', 'DESC');
                break;
            case 'today':
                $date = date('Y-m-d');
                $res
                    ->andWhere("i.delayDate = :date")
                    ->setParameter(':date', $date)
                    ->orderBy('performerName', 'DESC');
                break;
            case 'yesterday':
                $date = date('Y-m-d', mktime(0, 0, 0, date("m"), date('d') - 1, date('Y')));
                $res
                    ->andWhere("i.delayDate = :date")
                    ->setParameter(':date', $date)
                    ->orderBy('performerName', 'DESC');
                break;
            case 'tuesday':
                $days = 1;
                if (date("w") == 0) {
                    $days = 2;
                } elseif (date("w") == 6) {
                    $days = 3;
                } elseif (date("w") == 5) {
                    $days = 4;
                }
                $date = date('Y-m-d', mktime(0, 0, 0, date("m"), date('d') + $days, date('Y')));
                $res
                    ->andWhere("i.delayDate = :date")
                    ->setParameter(':date', $date)
                    ->orderBy('performerName', 'DESC');
                break;
            case 'wednesday':
                $days = 2;
                if (date("w") == 0) {
                    $days = 3;
                } elseif (date("w") == 6) {
                    $days = 4;
                } elseif (date("w") == 5) {
                    $days = 5;
                }
                $date = date('Y-m-d', mktime(0, 0, 0, date("m"), date('d') + $days, date('Y')));
                $res
                    ->andWhere("i.delayDate = :date")
                    ->setParameter(':date', $date)
                    ->orderBy('performerName', 'DESC');
                break;
            case 'thursday':
                $days = 3;
                if (date("w") == 0) {
                    $days = 4;
                } elseif (date("w") == 6) {
                    $days = 5;
                } elseif (date("w") == 5) {
                    $days = 6;
                }
                $date = date('Y-m-d', mktime(0, 0, 0, date("m"), date('d') + $days, date('Y')));
                $res
                    ->andWhere("i.delayDate = :date")
                    ->setParameter(':date', $date)
                    ->orderBy('performerName', 'DESC');
                break;
            case 'friday':
                $days = 4;
                if (date("w") == 0) {
                    $days = 5;
                } elseif (date("w") == 6) {
                    $days = 6;
                } elseif (date("w") == 5) {
                    $days = 7;
                }
                $date = date('Y-m-d', mktime(0, 0, 0, date("m"), date('d') + $days, date('Y')));
                $res
                    ->andWhere("i.delayDate = :date")
                    ->setParameter(':date', $date)
                    ->orderBy('performerName', 'DESC');
                break;
        }

        return $res->getQuery();
    }
